import {
  CREATE_STREAM,
  JOIN_STREAM,
  GET_STREAM,
  SEND_MESSAGE,
  CHOOSE_STREAM,
  DELETE_STREAM,
  UPDATE_STREAM
} from '../types'

export default (state, action) => {
  switch (action.type) {
    case GET_STREAM: 
      console.log('[reducer]', action.payload)
      return {
        ...state,
        streams: action.payload,
        current: state.streams[0]
      }
    case CHOOSE_STREAM: 
      return {
        ...state,
        current: action.payload
      }
    case SEND_MESSAGE: 
      return {
        ...state,
        messages: [...state.messages, action.payload]
      }
    case CREATE_STREAM: 
      return {
        ...state,
        streams: [...state.streams, action.payload]
      }
    case DELETE_STREAM:
      return {
        ...state,
        streams: state.streams.filter(stream => stream.id !== action.payload.id),
        current: state.streams[0]
      }
    default: 
      return {
        ...state
      }
  }
}
