import React, { useState, useEffect } from 'react'
import '../assests/css/index.css'
import axios from 'axios'

const Survey = () => {

  const [state, setState] = useState({
    data: [],
    index: 0
  })

  useEffect(() => {
    const config = {
      headers: {
        'x-auth-token': localStorage.getItem('token'),
      },
    };

    if (state.data.length == 0) {
      const data = axios.get('/api/v1/queries', config).then(res => {
        const data = res.data
        let temp = {...state}
        temp.data = data
        setState(temp)
      })
    }
  }, [])

  const next = () => {
    let temp = { ...state }
    temp.index++
    setState(temp)
  }
  const previous = () => {
    let temp = { ...state }
    temp.index--
    setState(temp)
  }

  const RenderSwitch = () => {
    const type = state.data[state.index].type
    const data = state.data[state.index]
    let jsx = null
    switch (type) {
      case 'free' : 
       return (
        <div className="free">
          <input type="text" />
        </div>
       )
      case 'scq' : 
        return (
          <div className="scq">
                            {
                              data.options.map((option, i) => (
                                <div className="option">
                                    <input type="radio" name={data.question} id={option} value={option} />
                                    <label for={option}>{option}</label>
                                    <br /> <br />
                                </div> 
                              ))
                            }
                        </div>
        )
      case 'mcq' :
        return (
          <div className="mcq">
                            {
                              data.options.map((option, i) => (
                                <div className="option">
                                    <input type="checkbox" name={data.question} id={option} value={option} />
                                    <label for={option}>{option}</label>
                                    <br /> <br />
                                </div> 
                              ))
                            }
                        </div>
        )
      default: 
        return <div></div>
    }
  }

  return (
    <div className="page-wrapper">
    <div className="content-wrapper">
        {
          state.data.length == 0 ? <div></div> : (
            <div className="box">
              <div className="question-space">
                  <div className="question" id="question">
                      <p>{state.data[state.index].question}</p>
                  </div>
              </div>
              <div className="answer-space" id="answer-space">
                {RenderSwitch()}
              </div>
              <div className="footer">
                  <div className="buttons">
                      <div className="btn-right"><button onClick={previous} disabled={state.index == 0 ? true : false}>Previous</button></div>
                      <div className="btn-left"><button onClick={next} disabled={state.index == state.data.length-1 ? true:false}>Next</button></div>
                  </div>
              </div>
          </div>
          )
        }
        <div className='final'>
          <div className='texts'>You've reached the end of the survey. Thanks for the patience!</div>
          <div><button>Submit</button></div>
        </div>
    </div>
</div>
  )
}

export default Survey
