import React, { useState, useContext } from 'react'
import StreamItems from './StreamItems'
import StreamContext from '../../context/streams/StreamContext'

const flex = {
  display: 'flex', 
  justifyContent: 'space-between'
}
const icon = {color: 'gray', marginTop: '10px', cursor: 'pointer'}

const StreamList = (props) => {
  const streamContext = useContext(StreamContext)
  const { createStream } = streamContext
  const { streams } = props

  const [title, setTitle] = useState('')
  const titleChange = e => {
    setTitle(e.target.value)
  }

  const onSubmit = e => {
    console.log(title)
    const formData = {
      id: 997,
      title
    }
    createStream(formData)
  }
  return (
    <div>
      <ul class="collection with-header">
        <li class="collection-header"><div style={flex}><h5>Streams</h5> <a className='modal-trigger' href='#create-stream'><i className='small material-icons' style={icon}>add</i></a> </div></li>
        {
          streams.map(stream => (<StreamItems stream={stream} />))
        }
      </ul> 

      <div id="create-stream" className="modal">
      <div class="modal-content">
        <h4>Create Stream</h4>
        <p><input name='title' value={title} onChange={titleChange} placeholder='Title for the stream' /></p>
      </div>
      <div class="modal-footer">
        <a href="#!" class="modal-close waves-effect waves-green btn" onClick={onSubmit}>Create</a>
      </div>
    </div>
  </div>
  )
}

export default StreamList
