import React, { useEffect, useContext } from 'react';
import './App.css'
import 'materialize-css/dist/css/materialize.min.css'
import M from 'materialize-css/dist/js/materialize.min.js'

import Navbar from './components/layouts/Navbar'
import TopCard from './components/layouts/TopCard'
import ChatArea from './components/layouts/ChatArea'

// Modals
import LoginModal from './components/pages/Login'
import RegModal from './components/pages/Register'
import EditModal from './components/pages/EditStreamModal'
import Lander from './components/pages/Lander'

import StreamState from './context/streams/StreamState'
import AuthState from './context/auth/AuthState'

import AuthContext from './context/auth/AuthContext'

const App = () => {
  useEffect(() => {
    M.AutoInit()
  }, [])

  const authContext = useContext(AuthContext)

  return (
    <div className="App">
      <AuthState>
        <StreamState>
          <Navbar />
          <div className='container'>
            <LoginModal />
            <RegModal />
            <EditModal />
            {
              authContext.isAuth ? (
                <Lander />
              ) : (
                <>
                <TopCard />
                <ChatArea />
                </>
              )
            }
          </div>
        </StreamState>
      </AuthState>
    </div>
  );
}

export default App;
