import React, { useState } from 'react'


const Login = () => {

  const [user, setUser] = useState({
    email: '',
    password: ''
  })

  const onChange = e => {
    setUser({
      ...user,
      [e.target.name]: e.target.value
    })
  }

  const {
    email, password
  } = user

  const onSubmit = () => {
    console.log(user)
  }

  return (
    <div>
        <div id="login-modal" class="modal">
        <div class="modal-content">
          <h4>Login</h4>
          <p><input onChange={onChange} name='email' placeholder='Email or Username' /></p>
          <p><input type='password' onChange={onChange} name='password' placeholder='Password' /></p>
        </div>
        <div class="modal-footer">
          <a href="#!" class="modal-close waves-effect waves-green btn-flat" onClick={onSubmit}>Login</a>
        </div>
      </div>
    </div>
  )
}

export default Login
