import axios from 'axios'
import io from 'socket.io-client'
import setAuthToken from '../utils/setAuthToken'
import { useSelector, useDispatch } from 'react-redux'
import store from '../store'

import {
  CREATE_STREAM,
  JOIN_STREAM,
  GET_STREAM,
  SEND_MESSAGE,
  CHOOSE_STREAM,
  DELETE_STREAM,
  UPDATE_STREAM,

  LOGIN,
  REGISTER
} from '../context/types'

const socket = io('https://collab-bin.herokuapp.com')
const user = store.getState().auth.user

// Auth actions
export const login = formData => (dispatch, getState) => {
  const config = {
    headers: {
      "Content-Type": "application/json"
    }
  }
  axios.post('https://collab-bin.herokuapp.com/auth/login', formData, config).then(res => {
    console.log(res.data)
    getUser()
    dispatch ( {
      type: LOGIN,
      payload: res.data
    })
  })

}

export const register = formData => (dispatch, getState) => {
  console.log('state', formData)
  const config = {
    headers: {
      "Content-Type": "application/json",
    }
  }
  axios.post('https://collab-bin.herokuapp.com/auth/register', formData, config).then(res => {
    console.log(res.data)
    dispatch ( {
      type: REGISTER,
      payload: formData
    })
  })

}

export const getUser = () => (dispatch, getState) => {
    setAuthToken(localStorage.token)
    axios.get('https://collab-bin.herokuapp.com/auth/getUser').then(res => {
      if (res.data) {
        console.log('[action]', res.data)
        dispatch ( {
          type: 'SET_USER',
          payload: res.data
        })
      } else {
        dispatch ( {
          type: 'ERROR_USER'
        })
      }
    }) 
}

// Stream actions
socket.on('new message', data => {
  renderMessages(data)
})

export const renderMessages = data => (dispatch, getState) => {
  dispatch ({
    type: 'RENDER_MESSAGES',
    payload: data
  }) 
}

export const getStream = () => (dispatch, getState) => {
  if (user) {
    axios.get(`https://collab-bin.herokuapp.com/streams/${user._id}`).then(res => {
      dispatch ({
        type: GET_STREAM,
        payload: res.data
      } )
    })
  }
}

export const resetCurrent = data => (dispatch, getState) => {
  console.log(data)
  dispatch ({
    type: 'SET_CURRENT',
    payload: data
  }) 
}

export const selectStream = stream => (dispatch, getState) => {
  dispatch ( {
    type: CHOOSE_STREAM,
    payload: stream
  } )
}

export const sendMessage = (obj) => (dispatch, getState) => {


  if (user) {

    const config = {
      headers: {
        'Content-Type': 'application/json'
      }
    }
    const { formData, current } = obj
    formData.sender_id = user._id
    formData.sender_name = user.name
    console.log(formData)

   axios.post('https://collab-bin.herokuapp.com/streams/send', formData, config).then(res => {
    dispatch ({
      type: SEND_MESSAGE,
      payload: obj
    })
   })
  }
}

export const createStream = title => (dispatch, getState) => {
  const config = {
    headers: {
      'Content-Type': 'application/json'
    }
  }

  if (user) {
    const formData = {
      title: title.title,
      owner_id: user._id,
      owner_name: user.name
    }
    axios.post('https://collab-bin.herokuapp.com/streams/create', formData, config).then(res => {
      dispatch ( {
        type: CREATE_STREAM,
        payload: formData
      })
    })

  }
}

export const deleteStream = formData => (dispatch, getState) => {
  dispatch ( {
    type: DELETE_STREAM,
    payload: formData
  })
}

export const updateStream = formData => (dispatch, getState) => {
  dispatch ( {
    type: UPDATE_STREAM,
    payload: formData
  })
}

export const joinStream = link => (dispatch, getState) => {
  const url = `https://collab-bin.herokuapp.com/streams/join/${link}/${user._id}`
  axios.get(url).then(res => {
    dispatch ( {
      type: JOIN_STREAM,
      payload: res.data.newStream
    })
  })
}
