import {
  LOGIN,
  REGISTER
} from '../types'

export default (state, action) => {
  switch (action.type) {
    case LOGIN: 
      localStorage.setItem('token', action.payload.token)
      return {
        ...state,
        user: action.payload,
        isAuth: true
      }
    case 'SET_USER':
      return {
        ...state,
        user: action.payload,
        isAuth: true
      }
    case 'ERROR_USER': 
      return {
        ...state,
        user: null,
        isAuth: false
      }
    default: 
      return {
        ...state
      }
  }
}
