import React, { useState } from 'react'
import '../assests/css/admin.css'

const CreateSurvey = () => {

  const [state, setState] = useState({
    title: '',
    questions: [{
      question: '',
      type: '',
      options: []
    }]
  })

  const handleChanges = (e, i) => {
    console.log(e.target.value)
  }

  const addOption = (e, i) => {
    console.log(i)
    let temp = { ...state }
    temp.questions[i].options.append('')
    setState(temp)
  }

  const addQuestion = e => {

  }

  return (
    <div className='page-wrapper'>
      <div className='content-wrapper'>
        <div className='box'>
          <div className='title'>
            <input type='text' name='title' onClick={handleChanges} />
          </div>
          <div className='questions'>
            <div className='header'>
              <h3>Questions</h3>
            </div>

            {
              state.questions.map((question, i) => (
                <div className='each-question'>
                  <div className='question-body'>
                  <div className='counter'>{i+1}. </div>
                    <input type='text' name='question' 
                    onClick={(e, i) => handleChanges(e, i)}
                    value={state.questions[i].question}
                    />
                  </div>
                  <div className='answer-type'>
                    <select name='type' onClick={handleChanges}>
                      <option value="free">Free Input</option>
                      <option value="scq">Single Choice</option>
                      <option value="mcq">Multiple Choice</option>
                    </select>
                  </div>
                  {
                    state.questions[i].options.map(option => (
                      <div className='options'>
                        <input type='text' name='options' onClick={handleChanges}/>
                      </div>
                    ))
                  }
                  <div className='add-btn'>
                    <button className='btn add' style={{
                      marginLeft: '5%',
                      marginTop: '10px'
                    }}
                    onClick={(e) => console.log(i)}
                    >Add</button>
                  </div>
                </div>
              ))
            }
          </div>

          <div className='add-btn'>
            <button className='btn add' onClick={addQuestion}>Add</button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default CreateSurvey
