import React from 'react'

const ChatBox = (props) => {
  const { messages } = props
  console.log(messages)
  return (
    <div className='chat-box'>
      <div className='messages'>
        {
          messages.map(message => ( message.sender == 'me' ? (<p className='bubbleGreen'>{message.text}</p>) : 
          (<p className='bubble Gray'>{message.text}</p>)))
        }
      </div>
    </div>
  )
}

export default ChatBox
