import React, { useReducer, useContext } from 'react'
import StreamReducer from './StreamReducer'
import StreamContext from './StreamContext'
import setAuthToken from '../../utils/setAuthToken'
import AuthContext from '../auth/AuthContext'

import axios from 'axios'

import {
  CREATE_STREAM,
  JOIN_STREAM,
  GET_STREAM,
  SEND_MESSAGE,
  CHOOSE_STREAM,
  DELETE_STREAM,
  UPDATE_STREAM
} from '../types'

const StreamState = props => {
  const initialState = {
    current: null,
    streams: [],
    messages: [
      {
        id: 1,
        sender: 'other',
        text: 'hey there buddz'
      },
      {
        id: 2,
        sender: 'me',
        text: 'brooh, been where??'
      },
      {
        id: 3,
        sender: 'other',
        text: 'lundun bro'
      },
      {
        id: 4,
        sender: 'me',
        text: 'welcome back, buddz'
      }
    ]
  }

  const [state, dispatch] = useReducer(StreamReducer, initialState)

  const authContext = useContext(AuthContext)
  const { user } = authContext

  const getStream = async () => {
    if (user) {
      const res = await axios.get(`http://localhost:5000/streams/${user._id}`)
      dispatch({
        type: GET_STREAM,
        payload: res.data
      })
    }
  }

  const selectStream = stream => {
    dispatch({
      type: CHOOSE_STREAM,
      payload: stream
    })
  }

  const sendMessage = async formData => {
    if (user) {

      const config = {
        headers: {
          'Content-Type': 'application/json'
        }
      }

      formData.sender_id = user._id
      formData.sender_name = user.name
      console.log(formData)

      await axios.post('http://localhost:5000/streams/send', formData, config)

      dispatch({
        type: SEND_MESSAGE,
        payload: formData
      })
    }
  }

  const createStream = async title => {
    const config = {
      headers: {
        'Content-Type': 'application/json'
      }
    }
    if (user) {
      const formData = {
        title: title.title,
        owner_id: user._id,
        owner_name: user.name
      }
      const res = await axios.post('http://localhost:5000/streams/create', formData, config)
      dispatch({
        type: CREATE_STREAM,
        payload: formData
      })
    }
  }

  const deleteStream = formData => {
    dispatch({
      type: DELETE_STREAM,
      payload: formData
    })
  }

  const updateStream = formData => {
    dispatch({
      type: UPDATE_STREAM,
      payload: formData
    })
  }

  const joinStream = async url => {
    const res = await axios.get(url)
    console.log(res.data)
    // dispatch({
    //   type: JOIN_STREAM,
    //   payload: res.data.newStream
    // })
  }

  return (
    <StreamContext.Provider 
      value={{
        streams: state.streams,
        messages: state.messages,
        current: state.current,
        getStream,
        sendMessage,
        selectStream,
        createStream,
        deleteStream,
        updateStream
      }}
    >
      { props.children }
    </StreamContext.Provider>
  )
}

export default StreamState
