import React, { useState, useEffect } from 'react'
import '../assests/css/index.css'
import axios from 'axios'

const Survey = () => {

  const [state, setState] = useState({
    data: [],
    index: 0
  })

  useEffect(() => {
    const config = {
      headers: {
        'x-auth-token': localStorage.getItem('token'),
      },
    };
    const data = axios.get('/api/v1/queries', config).then(res => {
      const data = res.data
      let temp = {...state}
      temp.data = data
      setState(temp)
    })
  })

  return (
    <div className="page-wrapper">
    <div className="content-wrapper">
        {
          state.data.length == 0 ? <div></div> : (
            <div className="box">
              <div className="question-space">
                  <div className="question" id="question">
                      <p>{state.data[index].question}</p>
                  </div>
              </div>
              <div className="answer-space" id="answer-space">

              </div>
              <div className="footer">
                  <div className="buttons">
                      <div className="btn-right"><button>Previous</button></div>
                      <div className="btn-left"><button id="next">Next</button></div>
                  </div>
              </div>
          </div>
          )
        }
    </div>
</div>
  )
}

export default Survey
