import React, { useContext, useState } from 'react'
import StreamContext from '../../context/streams/StreamContext'

const ChatBox = (props) => {
  const { messages } = props

  const streamContext = useContext(StreamContext)
  const { sendMessage, current, updateStream } = streamContext

  const [msg, setMsg] = useState('')

  const onSend = (e) => {
    sendMessage({
      id: 34,
      sender: 'me',
      text: msg
    })
    setMsg('')
  }

  const handleInput = (e) => {
    setMsg(e.target.value)
  }

  const [title, setTitle] = useState('')
  const titleChange = e => {
    setTitle(e.target.value)
  }

  const onSubmit = e => {
    console.log(title)
    const formData = {
      id: 997,
      title
    }
    updateStream(formData)
  }

  if (!current) {
    return (<p>Select a stream</p>)
  }
  return (
    <div className='chat-box'>
      <div className='above-messages'>
        <div className='left'>
          <div className='image'>
            <img src={`https://robohash.org/${current.id}?set=set4`} />
          </div>
          <div className='title'>
            <a href='#update-stream' className='modal-trigger'><h5>{ current.title }</h5></a>  
          </div>
        </div>
        <div className='right'>
            <i className='small material-icons'>add_circle_outline</i>
        </div>
      </div>
      <div className='messages'>
        {
          messages.map(message => ( message.sender == 'me' ? (<p className='Green'><span className='bubble'>{ message.text }</span></p>) : 
          (<p className='Gray'><span className='bubble'>{ message.text }</span></p>)))
        }
      </div>
      <div className='type-area'>
        <textarea name='msg' value={msg} onChange={handleInput}></textarea>
        <p><i class="material-icons right" onClick={onSend}>send</i></p>
      </div>

      <div id="update-stream" className="modal">
      <div class="modal-content">
        <h4>Create Stream</h4>
        <p><input name='title' value={title} onChange={titleChange} placeholder='Title for the stream' /></p>
      </div>
      <div class="modal-footer">
        <a href="#!" class="modal-close waves-effect waves-green btn" onClick={onSubmit}>Create</a>
      </div>
    </div>

    </div>
  )
}

export default ChatBox
