import React, { useState, useEffect } from 'react'
import '../assests/css/index.css'
import axios from 'axios'
import crypto from 'crypto-random-string'

const Survey = () => {

  const [state, setState] = useState({
    data: [],
    index: 0,
    end: false,
    answers: []
  })

  useEffect(() => {
    const config = {
      headers: {
        'x-auth-token': localStorage.getItem('token'),
      },
    };

    if (state.data.length == 0) {
      const data = axios.get('/api/v1/queries', config).then(res => {
        const data = res.data
        let temp = {...state}
        temp.data = data
        temp.answers = new Array(data.length).fill({
          index: null,
          type: null,
          value: ''
        })
        setState(temp)
      })
    }
  }, [])

  const next = () => {
    let temp = { ...state }
    temp.index++
    if (temp.index == temp.data.length-1) {
      temp.end = true
    }
    setState(temp)
  }
  const previous = () => {
    let temp = { ...state }
    temp.index--
    setState(temp)
  }

  const handleText = e => {
    console.log(e.target.value)
    let temp = { ...state }
    temp.answers[state.index] = {
      index: state.index,
      type: state.data[state.index].type,
      value: e.target.value
    }
    setState(temp)
  }

  const handleRadio = e => {
    let checked = []
    const checkBoxes = document.getElementsByTagName('input')
    for (let i=0; i<checkBoxes.length; i++) {
      if (checkBoxes[i].checked) {
        checked.push(checkBoxes[i].value)
      }
    }
    let temp = { ...state }
    temp.answers[state.index] = {
      index: state.index,
      type: state.data[state.index].type,
      value: checked
    }
    setState(temp)
  }

  const handleChecks = e => {
    let checked = []
    const checkBoxes = document.getElementsByTagName('input')
    for (let i=0; i<checkBoxes.length; i++) {
      if (checkBoxes[i].checked) {
        checked.push(checkBoxes[i].value)
      }
    }
    let temp = { ...state }
    temp.answers[state.index] = {
      index: state.index,
      type: state.data[state.index].type,
      value: checked
    }
    setState(temp)
  }

  const submit = () => {
    const user_id = crypto({ length: 6 })
    const response = {
      user_id,
      answers: state.answers
    }
    console.log(response)
  } 

  const RenderSwitch = () => {
    const type = state.data[state.index].type
    const data = state.data[state.index]
    let jsx = null
    switch (type) {
      case 'free' : 
       return (
        <div className="free">
          <input type="text" name={data.question} value={state.answers[state.index].value} onChange={handleText} />
        </div>
       )
      case 'scq' : 
        return (
          <div className="scq">
                            {
                              data.options.map((option, i) => (
                                <div className="option">
                                    <input 
                                    type="radio" 
                                    key={data.question} 
                                    name={data.question} 
                                    id={option} 
                                    onChange={handleChecks}
                                    />
                                    <label htmlFor={option}>{option}</label>
                                    <br /> <br />
                                </div> 
                              ))
                            }
                        </div>
        )
      case 'mcq' :
        return (
          <div className="mcq">
                            {
                              data.options.map((option, i) => (
                                <div className="option">
                                    <input type="checkbox" key={data.question} name={data.question} id={option} value={option} onChange={handleChecks}/>
                                    <label htmlFor={option}>{option}</label>
                                    <br /> <br />
                                </div> 
                              ))
                            }
                        </div>
        )
      default: 
        return <div></div>
    }
  }

  return (
    <div className="page-wrapper">
    <div className="content-wrapper">
        {
          state.data.length == 0 ? <div></div> : (
            <div className="box">
              <div className="question-space">
                  <div className="question" id="question">
                      <p>{state.data[state.index].question}</p>
                  </div>
              </div>
              <div className="answer-space" id="answer-space">
                {RenderSwitch()}
              </div>
              <div className="footer">
                  <div className="buttons">
                      <div className="btn-right">
                        {/* <button onClick={previous} disabled={state.index == 0 ? true : false}>Previous</button> */}
                        </div>
                      <div className="btn-left"><button onClick={next} disabled={state.index == state.data.length-1 ? true:false}>Next</button></div>
                  </div>
              </div>
          </div>
          )
        }

        {
          state.end ? <div className='final'>
          <p>
            <div className='texts'>You've reached the end of the survey. Thanks for the patience!</div>
            <br/>
            <div><button onClick={submit}>Submit</button></div>
          </p>
        </div> : null
        }
    </div>
</div>
  )
}

export default Survey
