import React, { useReducer } from 'react'
import StreamReducer from './StreamReducer'
import StreamContext from './StreamContext'
import {
  CREATE_STREAM,
  JOIN_STREAM,
  GET_STREAM
} from '../types'

const StreamState = props => {
  const initialState = {
    streams: [
      {
        id: 1,
        title: 'With the buddiez'
      },
      {
        id: 2,
        title: 'Ma home Bois'
      },
      {
        id: 3,
        title: 'Univ NiggaZ'
      },
      {
        id: 4,
        title: 'bad shitty creepZ'
      }
    ],
    messages: [
      {
        id: 1,
        sender: 'other',
        text: 'hey there buddz'
      },
      {
        id: 2,
        sender: 'me',
        text: 'brooh, been where??'
      },
      {
        id: 3,
        sender: 'other',
        text: 'lundun bro'
      },
      {
        id: 4,
        sender: 'me',
        text: 'welcome back, buddz'
      }
    ]
  }

  const [state, dispatch] = useReducer(StreamReducer, initialState)

  const getStream = () => {
    dispatch({
      type: GET_STREAM
    })
  }

  return (
    <StreamContext.Provider 
      value={{
        streams: state.streams,
        messages: state.messages,
        getStream
      }}
    >
      { props.children }
    </StreamContext.Provider>
  )
}

export default StreamState
